<section id="settings">
	<div class="close">
		<img src="assets/img/Fermer.svg" alt="close" class="close_button" />
	</div>

	<div class="lights_control">
		<div class="header">
			<h1>Lights</h1> <h2>Headlights</h2>
		</div>
		<div class="state">
			<ul>
				<li class="off active">OFF</li>
				<li class="parking">PARKING</li>
				<li class="auto">AUTO</li>
				<li class="on">ON</li>
			</ul>
		</div>
		<div class="options">
			<div class="drl active">
				<p>DRL</p>
			</div>
			<div class="fog">
				<p>FOG</p>
			</div>
			<div class="dome">
				<p>DOME</p>
			</div>
			<div class="ambient">
				<p>AMBIENT</p>
			</div>
		</div>
	</div>

	<div class="lock">
		<div class="front">
			<div class="title">
				<h1>Front Trunk</h1>
				<h2>Unlocked</h2>
			</div>
			<div class="icon">
				<img src="assets/img/Deverrouillage.svg" alt="light-preview" />
			</div>
		</div>
		<div class="left">
			<div class="bar"></div>
			<div class="title">
				<h1>Left Door</h1>
				<h2>Unlocked</h2>
			</div>
			<div class="icon">
				<img src="assets/img/Deverrouillage.svg" alt="light-preview" />
			</div>
		</div>
		
		<div class="right">
			<div class="bar"></div>
			<div class="title">
				<h1>Right Door</h1>
				<h2>Unlocked</h2>
			</div>
			<div class="icon">
				<img src="assets/img/Deverrouillage.svg" alt="light-preview" />
			</div>
		</div>

		<div class="back">
			<div class="icon">
				<img src="assets/img/Deverrouillage.svg" alt="light-preview" />
			</div>
			<div class="title">
				<h1>Back Door</h1>
				<h2>Unlocked</h2>
			</div>
		</div>

		<div class="charge">
			<div class="bar"></div>
			<div class="icon">
				<img src="assets/img/Deverrouillage.svg" alt="light-preview" />
			</div>
			<div class="title">
				<h1>Charge Port</h1>
				<h2>Unlocked</h2>
			</div>
		</div>
	</div>


	<div class="roof_open">

	</div>
	<div class="roof_close">

	</div>

	<div class="car">
		<div class="lights">
			<img src="assets/img/Light_Off.svg" alt="light-preview" class="light_preview" />
		</div>
		<svg height="110" width="110">
		   <line x1="0" y1="0" x2="110" y2="110" style="stroke:rgb(0,216,239);stroke-width:2"></line>
		</svg>
		<div class="car_skeleton">
			<img src="assets/img/Car.svg" alt="close" class="skeleton" />
		</div>
	</div>

	<div class="urgency">

		<div class="iconeU_off">

		</div>
		<div class="iconeU_on">

		</div>

		<p class="name">URGENCY LOCK</p>
		<p class="urgencyetat off">OFF</p>
	</div>

	<div class="blindage">
		<div class="urgencyicone iconeB_off">

		</div>
		<div class="urgencyicone iconeB_on">

		</div>
		<p class="name">BLINDAGE</p>
		<p class="blindageetat off">OFF</p>
	</div>



	<div class="menu">
		<div class="aside">
				<ul>
					<li class="general active"><img src="assets/img/Voiture.svg" alt="menu-car" /></li>
					<li class="driving noactive"><img src="assets/img/Navigation.svg" alt="navigation" /></li>
					<li class="display noactive"><img src="assets/img/Widget.svg" alt="car" /></li>
					<li class="charging noactive"><img src="assets/img/Batterie.svg" alt="battery" /></li>
				</ul>
		</div>
		<div class="content">
				<ul>
					<li class="txt_general active">General</li>
					<li class="txt_driving noactive">Driving</li>
					<li class="txt_displays noactive">Displays</li>
					<li class="txt_charging noactive">Charging</li>
				</ul>
		</div>
	</div>
</section>